import React, { useEffect, useState } from "react";
import {
  Box, TextField, MenuItem, Table, TableBody, TableCell, TableContainer,
  TableHead, TableRow, Paper, Button, Switch, Dialog, DialogTitle, DialogContent,
  DialogActions, IconButton, Typography, FormControlLabel
} from "@mui/material";
import { styled } from "@mui/material/styles";
import CloseIcon from "@mui/icons-material/Close";
import DeleteIcon from "@mui/icons-material/Delete";
import EditIcon from "@mui/icons-material/Edit";
import { Formik, Form, Field } from "formik";
import axios from "axios";
import InteviewPortal from "./InteviewPortal";

const BootstrapDialog = styled(Dialog)(({ theme }) => ({
  "& .MuiDialogContent-root": { padding: theme.spacing(2) },
  "& .MuiDialogActions-root": { padding: theme.spacing(1) }
}));

const Categary = () => {
  const [open, setOpen] = useState(false);
  const [category, setCategory] = useState("");
  const [list, setList] = useState([]);
  const [editId, setEditId] = useState(null);
  const [searchCat, setSearchCat] = useState("");
  const [subcategories, setSubcategories] = useState([]);
  
  const API_URL = "https://interviewback-ucb4.onrender.com/category/";
  const AUTH_TOKEN = "YOUR_AUTH_TOKEN";

  useEffect(() => { dataView(); }, []);

  const handleClickOpen = () => setOpen(true);
  const handleClose = () => setOpen(false);

  const dataView = () => {
    axios.get(API_URL, { headers: { Authorization: AUTH_TOKEN } })
      .then(res => setList(res.data.data))
      .catch(err => console.error(err));
  };

  const handleSubmit = (values, { resetForm }) => {
    const apiCall = editId ? axios.patch(`${API_URL}${editId}`, values) :
                            axios.post(`${API_URL}create`, values);
    
    apiCall.then(() => {
      dataView();
      resetForm();
      handleClose();
    }).catch(err => console.error(err));
  };

  const deleteData = (id) => {
    axios.delete(`${API_URL}${id}`, { headers: { Authorization: AUTH_TOKEN } })
      .then(() => dataView())
      .catch(err => console.error(err));
  };

  const searchCategory = (e) => {
    setSearchCat(e.target.value);
    axios.get(`${API_URL}?search=${e.target.value}`, { headers: { Authorization: AUTH_TOKEN } })
      .then(res => {
        setList(res.data.data);
        if (res.data.data.length > 0) sub(res.data.data[0]._id);
      })
      .catch(err => console.error(err));
  };

  const sub = (categoryId) => {
    axios.get(`${API_URL}${categoryId}`, { headers: { Authorization: AUTH_TOKEN } })
      .then(res => setSubcategories(res.data.subcategories || []))
      .catch(err => console.error(err));
  };

  const editData = (id, row) => {
    handleClickOpen();
    setCategory(row);
    setEditId(id);
  };

  return (
    <InteviewPortal>
      <Box sx={{ display: "flex", flexWrap: "wrap" }}>
        <TextField select value={searchCat} label="Search Category" sx={{ width: "80%" }} onChange={searchCategory}>
          {list.map(option => (
            <MenuItem key={option._id} value={option._id}>{option.categoryName}</MenuItem>
          ))}
        </TextField>
        <Button sx={{ backgroundColor: "#1976d2", color: "white", width: "20%" }} onClick={handleClickOpen}>ADD CATEGORY</Button>
      </Box>

      <BootstrapDialog onClose={handleClose} open={open}>
        <DialogTitle>Add Category</DialogTitle>
        <IconButton onClick={handleClose} sx={{ position: "absolute", right: 8, top: 8 }}><CloseIcon /></IconButton>
        <Formik initialValues={{ categoryName: category.categoryName || "" }} onSubmit={handleSubmit}>
          <Form>
            <DialogContent dividers>
              <Field as={TextField} name="categoryName" label="Category" fullWidth />
              <DialogActions>
                <Button sx={{ backgroundColor: "#1976d2", color: "white" }} type="submit">Submit</Button>
              </DialogActions>
            </DialogContent>
          </Form>
        </Formik>
      </BootstrapDialog>

      <TableContainer component={Paper}>
        <Table>
          <TableHead>
            <TableRow>
              <TableCell>No.</TableCell>
              <TableCell align="center">Category Name</TableCell>
              <TableCell align="center">Status</TableCell>
              <TableCell align="center">Delete</TableCell>
              <TableCell align="center">Update</TableCell>
            </TableRow>
          </TableHead>
          <TableBody>
            {list.map((row, i) => (
              <TableRow key={i}>
                <TableCell>{i + 1}</TableCell>
                <TableCell align="center">{row.categoryName}</TableCell>
                <TableCell align="center">
                  <FormControlLabel control={<Switch checked={row.status === 'on'} />} />
                </TableCell>
                <TableCell align="center"><IconButton onClick={() => deleteData(row._id)}><DeleteIcon /></IconButton></TableCell>
                <TableCell align="center"><IconButton onClick={() => editData(row._id, row)}><EditIcon /></IconButton></TableCell>
              </TableRow>
            ))}
          </TableBody>
        </Table>
      </TableContainer>
    </InteviewPortal>
  );
};

export default Categary;
