import React, { useEffect, useState } from "react";
import Box from "@mui/material/Box";
import TextField from "@mui/material/TextField";
import MenuItem from "@mui/material/MenuItem";
import { styled } from "@mui/material/styles";
import Table from "@mui/material/Table";
import TableBody from "@mui/material/TableBody";
import TableCell, { tableCellClasses } from "@mui/material/TableCell";
import TableContainer from "@mui/material/TableContainer";
import TableHead from "@mui/material/TableHead";
import TableRow from "@mui/material/TableRow";
import Paper from "@mui/material/Paper";
import Button from "@mui/material/Button";
import Switch from '@mui/material/Switch';
import { Formik, Form, Field } from "formik";
import Dialog from "@mui/material/Dialog";
import DialogTitle from "@mui/material/DialogTitle";
import DialogContent from "@mui/material/DialogContent";
import DialogActions from "@mui/material/DialogActions";
import IconButton from "@mui/material/IconButton";
import CloseIcon from "@mui/icons-material/Close";
import InteviewPortal from "./InteviewPortal";
import DeleteIcon from '@mui/icons-material/Delete';
import EditIcon from '@mui/icons-material/Edit';
import axios from "axios";
import { FormControlLabel } from "@mui/material";

const primaryColor = "#6a1b9a";

const BootstrapDialog = styled(Dialog)(({ theme }) => ({
  "& .MuiDialogContent-root": {
    padding: theme.spacing(3),
    backgroundColor: "#f3f0fa"
  },
  "& .MuiDialogActions-root": {
    padding: theme.spacing(2),
    backgroundColor: "#eee"
  },
}));

const StyledTableCell = styled(TableCell)(({ theme }) => ({
  [`&.${tableCellClasses.head}`]: {
    background: "linear-gradient(to right, #6a1b9a, #8e24aa)",
    color: theme.palette.common.white,
    fontWeight: 'bold',
    fontSize: '16px'
  },
  [`&.${tableCellClasses.body}`]: {
    fontSize: 14,
    color: "#333",
  },
}));

const StyledTableRow = styled(TableRow)(({ theme }) => ({
  "&:nth-of-type(odd)": {
    backgroundColor: theme.palette.action.hover,
  },
  "&:last-child td, &:last-child th": {
    border: 0,
  },
}));

const Categary = () => {
  const [open, setOpen] = useState(false);
  const [ini, setIni] = useState({ categoryName: '' });
  const [list, setList] = useState([]);
  const [editId, setEditId] = useState(null);
  const [serchcat, setSearchCat] = useState('');

  const handleClickOpen = () => setOpen(true);
  const handleClose = () => setOpen(false);

  const handleSubmit = (values, { resetForm }) => {
    if (editId != null) {
      axios.patch(`https://interviewback-ucb4.onrender.com/category/${editId}`, values, {
        headers: { Authorization: "TOKEN" }
      })
        .then(() => dataView())
        .catch(console.error);

      setEditId(null);
    } else {
      axios.post('https://interviewback-ucb4.onrender.com/category/create', values, {
        headers: { Authorization: "TOKEN" }
      })
        .then(() => dataView())
        .catch(console.error);
    }

    resetForm();
    setIni({ categoryName: '' });
    handleClose();
  };

  const dataView = () => {
    axios.get('https://interviewback-ucb4.onrender.com/category/', {
      headers: { Authorization: "TOKEN" }
    })
      .then((res) => setList(res.data.data))
      .catch(console.error);
  };

  const deleteData = (id) => {
    axios.delete(`https://interviewback-ucb4.onrender.com/category/${id}`, {
      headers: { Authorization: "TOKEN" }
    })
      .then(() => dataView())
      .catch(console.error);
  };

  const editData = (id, row) => {
    handleClickOpen();
    setIni(row);
    setEditId(id);
  };

  const searchCategory = (e) => {
    const value = e.target.value;
    setSearchCat(value);

    axios.get(`https://interviewback-ucb4.onrender.com/category/?search=${value}`, {
      headers: { Authorization: "TOKEN" }
    })
      .then((res) => setList(res.data.data))
      .catch(console.error);
  };

  const allSubCatUpdate = (filteredSubcategories) => {
    filteredSubcategories.forEach((sub) => {
      axios.patch(`https://interviewback-ucb4.onrender.com/subcategory/${sub._id}`, {
        status: sub.status === 'on' ? 'off' : 'on'
      }, {
        headers: { Authorization: "TOKEN" }
      }).catch(console.error);
    });
  };

  const sub = (id) => {
    axios.get('https://interviewback-ucb4.onrender.com/subcategory/', {
      headers: { Authorization: "TOKEN" }
    })
      .then((res) => {
        const filtered = res.data.data.filter((item) => item.categoryID?._id === id);
        allSubCatUpdate(filtered);
      })
      .catch(console.error);
  };

  const handleswitch = (e, id) => {
    axios.patch(`https://interviewback-ucb4.onrender.com/category/${id}`, {
      status: e.target.checked ? 'on' : 'off'
    }, {
      headers: { Authorization: "TOKEN" }
    })
      .then(() => {
        dataView();
        sub(id);
      })
      .catch(console.error);
  };

  useEffect(() => {
    dataView();
  }, []);

  return (
    <InteviewPortal>
      <Box sx={{ width: "100%", display: "flex", flexWrap: "wrap" }}>
        <Box component="form" sx={{ width: "80%" }} noValidate autoComplete="off">
          <TextField
            value={serchcat}
            label="Search Category"
            onChange={searchCategory}
            sx={{ width: "100%", backgroundColor: "#fff", borderRadius: 1, boxShadow: 1 }}
          />
        </Box>
        <Box sx={{ width: "20%", display: "flex", justifyContent: "center", padding: "10px 0" }}>
          <Button
            sx={{ backgroundColor: primaryColor, color: "white", '&:hover': { backgroundColor: "#4a148c" } }}
            onClick={handleClickOpen}
          >
            ADD CATEGORY
          </Button>
        </Box>
      </Box>

      <BootstrapDialog onClose={handleClose} open={open}>
        <DialogTitle>Add Category</DialogTitle>
        <IconButton onClick={handleClose} sx={{ position: "absolute", right: 8, top: 8 }}>
          <CloseIcon />
        </IconButton>
        <Formik enableReinitialize initialValues={ini} onSubmit={handleSubmit}>
          <Form>
            <DialogContent dividers>
              <Field as={TextField} name="categoryName" label="Category" fullWidth sx={{ mb: 2 }} />
              <DialogActions>
                <Button type="submit" sx={{ backgroundColor: primaryColor, color: "white" }}>Submit</Button>
              </DialogActions>
            </DialogContent>
          </Form>
        </Formik>
      </BootstrapDialog>

      <TableContainer component={Paper} sx={{ borderRadius: 2, boxShadow: 3 }}>
        <Table sx={{ minWidth: 700 }}>
          <TableHead>
            <TableRow>
              <StyledTableCell>No.</StyledTableCell>
              <StyledTableCell align="center">Category Name</StyledTableCell>
              <StyledTableCell align="center">Status</StyledTableCell>
              <StyledTableCell align="center">Delete</StyledTableCell>
              <StyledTableCell align="center">Update</StyledTableCell>
            </TableRow>
          </TableHead>
          <TableBody>
            {list.map((row, i) => (
              <StyledTableRow key={row._id}>
                <StyledTableCell>{i + 1}</StyledTableCell>
                <StyledTableCell align="center">{row.categoryName}</StyledTableCell>
                <StyledTableCell align="center">
                  <FormControlLabel
                    control={<Switch checked={row.status === 'on'}
                      onChange={(e) => handleswitch(e, row._id)}
                      sx={{ '& .MuiSwitch-switchBase.Mui-checked': { color: primaryColor }, '& .MuiSwitch-track': { backgroundColor: primaryColor } }}
                    />}
                  />
                </StyledTableCell>
                <StyledTableCell align="center">
                  <IconButton onClick={() => deleteData(row._id)}><DeleteIcon /></IconButton>
                </StyledTableCell>
                <StyledTableCell align="center">
                  <IconButton onClick={() => editData(row._id, row)}><EditIcon /></IconButton>
                </StyledTableCell>
              </StyledTableRow>
            ))}
          </TableBody>
        </Table>
      </TableContainer>
    </InteviewPortal>
  );
};

export default Categary;
