    const url = editId
      ? `https://interviewback-ucb4.onrender.com/category/${editId}`
      : 'https://interviewback-ucb4.onrender.com/category/create';

    const method = editId ? axios.patch : axios.post;

    method(url, values, {
      headers: {
        Authorization:
          "eyJhbGciOiJIUzI1NiIsInR5cCI6IkpXVCJ9..."
      },
    })
      .then(() => {
        console.log(editId ? 'edit success' : 'create success');
        dataView();
      })
      .catch(console.log);

    setIni({ categoryName: '' });
    setEditId(null);
    resetForm();
    handleClose();
  };

  function dataView() {
    axios.get('https://interviewback-ucb4.onrender.com/category/', {
      headers: {
        Authorization: "eyJhbGciOiJIUzI1NiIsInR5cCI6IkpXVCJ9..."
      }
    })
      .then(res => setList(res.data.data))
      .catch(console.log);
  }

  const deleteData = (id) => {
    axios.delete(`https://interviewback-ucb4.onrender.com/category/${id}`, {
      headers: {
        Authorization: "eyJhbGciOiJIUzI1NiIsInR5cCI6IkpXVCJ9..."
      }
    })
      .then(() => {
        console.log('delete success');
        dataView();
      })
      .catch(console.log);
  };

  const editData = (id, row) => {
    handleClickOpen();
    setIni(row);
    setEditId(id);
  };

  const searchCategory = (e) => {
    const value = e.target.value;
    setSearchCat(value);
    axios.get(`https://interviewback-ucb4.onrender.com/category/?search=${value}`, {
      headers: {
        Authorization: "eyJhbGciOiJIUzI1NiIsInR5cCI6IkpXVCJ9..."
      }
    })
      .then(res => setList(res.data.data))
      .catch(console.log);
  };

  const allSubCatUpdate = (filteredSubcategories) => {
    for (let sub of filteredSubcategories) {
      axios.patch(`https://interviewback-ucb4.onrender.com/subcategory/${sub._id}`, {
        status: sub.status === 'on' ? 'off' : 'on',
      }, {
        headers: {
          Authorization: "eyJhbGciOiJIUzI1NiIsInR5cCI6IkpXVCJ9..."
        }
      }).catch(console.log);
    }
  };

  const sub = (id) => {
    axios.get('https://interviewback-ucb4.onrender.com/subcategory/', {
      headers: {
        Authorization: "eyJhbGciOiJIUzI1NiIsInR5cCI6IkpXVCJ9..."
      }
    }).then((res) => {
      const filtered = res.data.data.filter((item) => item.categoryID?._id === id);
      allSubCatUpdate(filtered);
    }).catch(console.log);
  };

  const handleswitch = (e, id) => {
    axios.patch(`https://interviewback-ucb4.onrender.com/category/${id}`, {
      status: e.target.checked ? 'on' : 'off',
    }, {
      headers: {
        Authorization: "eyJhbGciOiJIUzI1NiIsInR5cCI6IkpXVCJ9..."
      }
    })
      .then(() => {
        dataView();
        sub(id);
      })
      .catch(console.log);
  };

  useEffect(() => {
    dataView();
  }, []);

  return (
    <InteviewPortal>
      <Box sx={{ display: "flex", flexWrap: "wrap" }}>
        <Box component="form" sx={{ width: { lg: "80%", xs: "100%" } }} noValidate autoComplete="off">
          <TextField
            value={serchcat}
            label="Search Category"
            sx={{ width: "100%" }}
            onChange={searchCategory}
          />
        </Box>

        <Box sx={{ width: { lg: "20%", xs: "100%" }, display: "flex", justifyContent: "center", padding: "10px 0" }}>
          <Button
            sx={{
              backgroundColor: "#0d47a1",
              color: "white",
              width: "90%",
              height: '50px',
              '&:hover': {
                backgroundColor: "#1565c0"
              }
            }}
            variant="contained"
            onClick={handleClickOpen}
          >
            ADD CATEGORY
          </Button>

          <BootstrapDialog onClose={handleClose} open={open}>
            <DialogTitle
              sx={{
                backgroundColor: "#0d47a1",
                color: "white",
                fontWeight: 600,
                m: 0,
                p: 2
              }}
              id="customized-dialog-title"
            >
              Add Category
            </DialogTitle>
            <IconButton
              aria-label="close"
              onClick={handleClose}
              sx={{
                position: "absolute",
                right: 8,
                top: 8,
                color: (theme) => theme.palette.grey[500],
              }}
            >
              <CloseIcon />
            </IconButton>

            <Formik enableReinitialize initialValues={ini} onSubmit={handleSubmit}>
              <Form>
                <DialogContent dividers>
                  <Field
                    as={TextField}
                    name="categoryName"
                    label="Category"
                    fullWidth
                    variant="outlined"
                    sx={{ mt: 2, mb: 2 }}
                  />
                  <DialogActions>
                    <Button
                      sx={{
                        backgroundColor: "#0d47a1",
                        color: "white",
                        '&:hover': {
                          backgroundColor: "#1565c0"
                        }
                      }}
                      type="submit"
                    >
                      Submit
                    </Button>
                  </DialogActions>
                </DialogContent>
              </Form>
            </Formik>
          </BootstrapDialog>
        </Box>
      </Box>

      <TableContainer component={Paper}>
        <Table sx={{ minWidth: 700 }} aria-label="customized table">
          <TableHead>
            <TableRow>
              <StyledTableCell>No.</StyledTableCell>
              <StyledTableCell align="center">Category Name</StyledTableCell>
              <StyledTableCell align="center">Status</StyledTableCell>
              <StyledTableCell align="center">Delete</StyledTableCell>
              <StyledTableCell align="center">Update</StyledTableCell>
            </TableRow>
          </TableHead>
          <TableBody>
            {list.map((row, i) => (
              <StyledTableRow key={i}>
                <StyledTableCell>{i + 1}</StyledTableCell>
                <StyledTableCell align="center">{row.categoryName}</StyledTableCell>
                <StyledTableCell align="center">
                  <FormControlLabel
                    control={<Switch checked={row.status === 'on'} />}
                    onChange={(e) => handleswitch(e, row._id)}
                  />
                </StyledTableCell>
                <StyledTableCell align="center">
                  <button onClick={() => deleteData(row._id)}><DeleteIcon /></button>
                </StyledTableCell>
                <StyledTableCell align="center">
                  <button onClick={() => editData(row._id, row)}><EditIcon /></button>
                </StyledTableCell>
              </StyledTableRow>
            ))}
          </TableBody>
        </Table>
      </TableContainer>
    </InteviewPortal>
  );
};

export default Categary;
