import { FormControlLabel } from "@mui/material";

const BootstrapDialog = styled(Dialog)(({ theme }) => ({
  "& .MuiDialogContent-root": {
    padding: theme.spacing(2),
  },
  "& .MuiDialogActions-root": {
    padding: theme.spacing(1),
  },
}));

const StyledTableCell = styled(TableCell)(({ theme }) => ({
  [`&.${tableCellClasses.head}`]: {
    backgroundColor: "#1976d2",
    color: theme.palette.common.white,
  },
  [`&.${tableCellClasses.body}`]: {
    fontSize: 14,
  },
}));

const StyledTableRow = styled(TableRow)(({ theme }) => ({
  "&:nth-of-type(odd)": {
    backgroundColor: theme.palette.action.hover,
  },
  "&:last-child td, &:last-child th": {
    border: 0,
  },
}));

const Categary = () => {
  const [open, setOpen] = React.useState(false);
  const handleClickOpen = () => setOpen(true);
  const handleClose = () => setOpen(false);
  const [ini, setIni] = useState({ categoryName: '' });
  const [list, setList] = useState([]);
  const [editId, setEditId] = useState(null);
  const [serchcat, setSearchCat] = useState('');

  const handleSubmit = (values, { resetForm }) => {
    const token = "eyJhbGciOiJIUzI1NiIsInR5cCI6IkpXVCJ9.eyJpZCI6IjY3ODA4YTE3MzRkZGY2ZjZlZGUyNTRmMSIsImlhdCI6MTc0MjYyMDUwM30._S8-zRqSVebO8IljXmp3vSa-IOpV2_bp-USjSzuVFP8";
    const headers = { Authorization: token };

    const url = editId
      ? `https://interviewback-ucb4.onrender.com/category/${editId}`
      : 'https://interviewback-ucb4.onrender.com/category/create';

    const method = editId ? axios.patch : axios.post;

    method(url, values, { headers })
      .then(() => {
        console.log(editId ? 'edit success' : 'create success');
        dataView();
        setIni({ categoryName: '' });
        handleClose();
        resetForm();
      })
      .catch(console.log);
  };

  const dataView = () => {
    axios.get('https://interviewback-ucb4.onrender.com/category/', {
      headers: { Authorization: "..." }
    }).then(res => setList(res.data.data)).catch(console.log);
  };

  const deleteData = (id) => {
    axios.delete(`https://interviewback-ucb4.onrender.com/category/${id}`, {
      headers: { Authorization: "..." }
    }).then(() => dataView()).catch(console.log);
  };

  const editData = (id, row) => {
    handleClickOpen();
    setIni(row);
    setEditId(id);
  };

  const searchCategory = (e) => {
    const value = e.target.value;
    setSearchCat(value);
    axios.get(`https://interviewback-ucb4.onrender.com/category/?search=${value}`, {
      headers: { Authorization: "..." }
    }).then(res => setList(res.data.data)).catch(console.error);
  };

  const allSubCatUpdate = (filteredSubcategories) => {
    for (let sub of filteredSubcategories) {
      axios.patch(`https://interviewback-ucb4.onrender.com/subcategory/${sub._id}`, {
        status: sub.status === 'on' ? 'off' : 'on',
      }, {
        headers: { Authorization: "..." }
      }).catch(console.error);
    }
  };

  const sub = (id) => {
    axios.get('https://interviewback-ucb4.onrender.com/subcategory/', {
      headers: { Authorization: "..." }
    }).then(res => {
      const filtered = res.data.data.filter(item => item.categoryID?._id === id);
      allSubCatUpdate(filtered);
    }).catch(console.log);
  };

  const handleswitch = (e, id) => {
    axios.patch(`https://interviewback-ucb4.onrender.com/category/${id}`, {
      status: e.target.checked ? 'on' : 'off'
    }, {
      headers: { Authorization: "..." }
    }).then(() => {
      dataView();
      sub(id);
    }).catch(console.log);
  };

  useEffect(() => { dataView(); }, []);

  return (
    <InteviewPortal>
      <Box sx={{ width: "100%", display: "flex", flexWrap: "wrap" }}>
        <Box component="form" sx={{ width: { lg: "80%" } }}>
          <TextField value={serchcat} label="Search Category" sx={{ width: "100%" }} onChange={searchCategory} />
        </Box>
        <Box sx={{ width: { lg: "20%" }, display: "flex", justifyContent: "center", padding: "10px 0px" }}>
          <Button sx={{ background: 'linear-gradient(135deg, #4fc3f7 0%, #0288d1 100%)', color: "white", width: "90%", height: '50px' }} variant="outlined" onClick={handleClickOpen}>
            ADD CATAGARY
          </Button>
          <BootstrapDialog onClose={handleClose} open={open}>
            <DialogTitle id="customized-dialog-title">Add Category</DialogTitle>
            <IconButton aria-label="close" onClick={handleClose} sx={{ position: "absolute", right: 8, top: 8, color: 'gray' }}>
              <CloseIcon />
            </IconButton>
            <Formik enableReinitialize initialValues={ini} onSubmit={handleSubmit}>
              <Form>
                <DialogContent dividers>
                  <Field as={TextField} name="categoryName" label="Category" fullWidth />
                  <DialogActions>
                    <Button sx={{ background: 'linear-gradient(135deg, #4fc3f7 0%, #0288d1 100%)', color: "white" }} autoFocus type="submit">
                      Submit
                    </Button>
                  </DialogActions>
                </DialogContent>
              </Form>
            </Formik>
          </BootstrapDialog>
        </Box>
      </Box>

      <TableContainer component={Paper}>
        <Table sx={{ minWidth: 700 }}>
          <TableHead>
            <TableRow>
              <StyledTableCell>No.</StyledTableCell>
              <StyledTableCell align="center">Category Name</StyledTableCell>
              <StyledTableCell align="center">Status</StyledTableCell>
              <StyledTableCell align="center">Delete</StyledTableCell>
              <StyledTableCell align="center">Update</StyledTableCell>
            </TableRow>
          </TableHead>
          <TableBody>
            {list.map((row, i) => (
              <StyledTableRow key={i}>
                <StyledTableCell>{i + 1}</StyledTableCell>
                <StyledTableCell align="center">{row.categoryName}</StyledTableCell>
                <StyledTableCell align="center">
                  <FormControlLabel control={<Switch checked={row.status === 'on'} onChange={(e) => handleswitch(e, row._id)} />} label={row.status} />
                </StyledTableCell>
                <StyledTableCell align="center">
                  <IconButton onClick={() => deleteData(row._id)} sx={{ color: 'red' }}><DeleteIcon /></IconButton>
                </StyledTableCell>
                <StyledTableCell align="center">
                  <IconButton onClick={() => editData(row._id, row)} sx={{ color: 'green' }}><EditIcon /></IconButton>
                </StyledTableCell>
              </StyledTableRow>
            ))}
          </TableBody>
        </Table>
      </TableContainer>
    </InteviewPortal>
  );
};

export default Categary;
