import React, { useEffect, useState } from 'react';
import {
  Box, TextField, MenuItem, Table, TableBody, TableCell, TableContainer,
  TableHead, TableRow, Paper, Button, Switch, Dialog, DialogTitle, DialogContent,
  DialogActions, IconButton, Typography, FormControl, FormControlLabel,
  InputLabel, Select
} from '@mui/material';

import { styled } from '@mui/material/styles';
import CloseIcon from '@mui/icons-material/Close';
import DeleteIcon from '@mui/icons-material/Delete';
import EditIcon from '@mui/icons-material/Edit';
import { Formik, Form, Field } from 'formik';
import axios from 'axios';
import InteviewPortal from './InteviewPortal';

// Custom Dialog
const BootstrapDialog = styled(Dialog)(({ theme }) => ({
  '& .MuiDialogContent-root': { padding: theme.spacing(2) },
  '& .MuiDialogActions-root': { padding: theme.spacing(1) },
}));

// Styled Table Cells
const StyledTableCell = styled(TableCell)(({ theme }) => ({
  [`&.${TableCell.head}`]: {
    backgroundColor: '#0d47a1',
    color: theme.palette.common.white,
    fontWeight: 'bold',
    fontSize: 16,
  },
  [`&.${TableCell.body}`]: {
    fontSize: 14,
  },
}));

const StyledTableRow = styled(TableRow)(({ theme }) => ({
  '&:nth-of-type(odd)': {
    backgroundColor: theme.palette.action.hover,
  },
  '&:hover': {
    backgroundColor: '#e3f2fd',
  },
}));

const SubCatagary = () => {
  const [open, setOpen] = useState(false);
  const [catId, setcatId] = useState('');
  const [ini, setIni] = useState({ subCategoryname: '', categoryID: '' });
  const [checked, setChecked] = useState(true);
  const [list, setList] = useState([]);
  const [list1, setList1] = useState([]);
  const [editId, setEditId] = useState(null);
  const [serchsubcat, setserchSubcat] = useState('');

  const handleClickOpen = () => {
    if (!editId) {
      setIni({ subCategoryname: '', categoryID: '' });
    }
    setcatId('');
    setOpen(true);
  };

  const handleClose = () => {
    setOpen(false);
  };

  const handleSubmit = (values, { resetForm }) => {
    const url = editId
      ? `https://interviewback-ucb4.onrender.com/subcategory/${editId}`
      : 'https://interviewback-ucb4.onrender.com/subcategory/create';

    const method = editId ? axios.patch : axios.post;

    method(url, values, {
      headers: {
        Authorization: "Bearer YOUR_JWT_TOKEN_HERE"
      }
    })
      .then(() => {
        dataView();
        resetForm();
        setEditId(null);
        handleClose();
      })
      .catch(console.error);
  };

  const dataView = () => {
    axios.get('https://interviewback-ucb4.onrender.com/subcategory/', {
      headers: {
        Authorization: "Bearer YOUR_JWT_TOKEN_HERE"
      }
    })
      .then((res) => setList1(res.data.data))
      .catch(console.error);
  };

  const handleFillcatagary = () => {
    axios.get('https://interviewback-ucb4.onrender.com/category/', {
      headers: {
        Authorization: "Bearer YOUR_JWT_TOKEN_HERE"
      }
    })
      .then((res) => setList(res.data.data))
      .catch(console.error);
  };

  const deleteData = (id) => {
    axios.delete(`https://interviewback-ucb4.onrender.com/subcategory/${id}`, {
      headers: {
        Authorization: "Bearer YOUR_JWT_TOKEN_HERE"
      }
    })
      .then(() => dataView())
      .catch(console.error);
  };

  const editData = (id, row) => {
    handleClickOpen();
    setEditId(id);
    setIni({
      subCategoryname: row.subCategoryname,
      categoryID: row.categoryID?._id || '',
    });
  };

  const serchSubcatagary = (e) => {
    const value = e.target.value;
    setserchSubcat(value);

    axios.get(`https://interviewback-ucb4.onrender.com/subcategory/?search=${value}`, {
      headers: {
        Authorization: "Bearer YOUR_JWT_TOKEN_HERE"
      }
    })
      .then((res) => setList1(res.data.data))
      .catch(console.error);
  };

  const handleSwith = (e, id) => {
    axios.patch(`https://interviewback-ucb4.onrender.com/subcategory/${id}`, {
      status: e.target.checked ? 'on' : 'off'
    }, {
      headers: {
        Authorization: "Bearer YOUR_JWT_TOKEN_HERE"
      }
    })
      .then(() => dataView())
      .catch(console.error);
  };

  useEffect(() => {
    dataView();
    handleFillcatagary();
  }, []);

  return (
    <InteviewPortal>
      <Box sx={{ display: 'flex', flexWrap: 'wrap', gap: 2, mb: 2 }}>
        <TextField
          label="Search Subcategory"
          fullWidth
          onChange={serchSubcatagary}
          value={serchsubcat}
          select
        >
          {list1.map((option) => (
            <MenuItem key={option._id} value={option.subCategoryname}>
              {option.subCategoryname}
            </MenuItem>
          ))}
        </TextField>

        <Button
          variant="contained"
          color="primary"
          onClick={handleClickOpen}
          sx={{ whiteSpace: 'nowrap', height: '56px' }}
        >
          ADD SUB-CATEGORY
        </Button>
      </Box>

      <BootstrapDialog open={open} onClose={handleClose}>
        <DialogTitle>
          {editId ? "Update Subcategory" : "Add Subcategory"}
          <IconButton onClick={handleClose} sx={{ position: 'absolute', right: 8, top: 8 }}>
            <CloseIcon />
          </IconButton>
        </DialogTitle>

        <Formik enableReinitialize initialValues={ini} onSubmit={handleSubmit}>
          {({ setFieldValue, values }) => (
            <Form>
              <DialogContent dividers>
                <Field as={TextField} fullWidth name="subCategoryname" label="Subcategory" sx={{ mb: 2 }} />
                <FormControl fullWidth>
                  <InputLabel>Category</InputLabel>
                  <Select
                    name="categoryID"
                    label="Category"
                    value={values.categoryID}
                    onChange={(e) => setFieldValue("categoryID", e.target.value)}
                  >
                    {list.map((row) => (
                      <MenuItem key={row._id} value={row._id}>
                        {row.categoryName}
                      </MenuItem>
                    ))}
                  </Select>
                </FormControl>
              </DialogContent>
              <DialogActions>
                <Button type="submit" variant="contained" color="primary">Submit</Button>
              </DialogActions>
            </Form>
          )}
        </Formik>
      </BootstrapDialog>

      <TableContainer component={Paper}>
        <Table>
          <TableHead>
            <TableRow>
              <StyledTableCell>No.</StyledTableCell>
              <StyledTableCell align="center">Subcategory Name</StyledTableCell>
              <StyledTableCell align="center">Category Name</StyledTableCell>
              <StyledTableCell align="center">Status</StyledTableCell>
              <StyledTableCell align="center">Delete</StyledTableCell>
              <StyledTableCell align="center">Update</StyledTableCell>
            </TableRow>
          </TableHead>
          <TableBody>
            {list1.map((row, i) => (
              <StyledTableRow key={row._id}>
                <StyledTableCell>{i + 1}</StyledTableCell>
                <StyledTableCell align="center">{row.subCategoryname}</StyledTableCell>
                <StyledTableCell align="center">{row.categoryID?.categoryName}</StyledTableCell>
                <StyledTableCell align="center">
                  <FormControlLabel
                    control={
                      <Switch
                        checked={row.status === 'on'}
                        onChange={(e) => handleSwith(e, row._id)}
                      />
                    }
                  />
                </StyledTableCell>
                <StyledTableCell align="center">
                  <IconButton onClick={() => deleteData(row._id)} color="error">
                    <DeleteIcon />
                  </IconButton>
                </StyledTableCell>
                <StyledTableCell align="center">
                  <IconButton onClick={() => editData(row._id, row)} color="primary">
                    <EditIcon />
                  </IconButton>
                </StyledTableCell>
              </StyledTableRow>
            ))}
          </TableBody>
        </Table>
      </TableContainer>
    </InteviewPortal>
  );
};

export default SubCatagary;
