import React from 'react';
import PropTypes from 'prop-types';
import { AppBar, Box, CssBaseline, Divider, Drawer, IconButton, List, ListItem, ListItemButton, ListItemIcon, ListItemText, Toolbar, Typography, Link } from '@mui/material';
import { Menu as MenuIcon, SpaceDashboard as SpaceDashboardIcon, Category as CategoryIcon, ControlPointDuplicate as ControlPointDuplicateIcon, HelpOutline as HelpOutlineIcon, MeetingRoom as MeetingRoomIcon } from '@mui/icons-material';

const drawerWidth = 260;

const InterviewPortal = ({ children }) => {
  const [mobileOpen, setMobileOpen] = React.useState(false);
  const [isClosing, setIsClosing] = React.useState(false);

  const handleDrawerToggle = () => {
    if (!isClosing) {
      setMobileOpen(!mobileOpen);
    }
  };

  const handleDrawerClose = () => {
    setIsClosing(true);
    setMobileOpen(false);
  };

  const handleDrawerTransitionEnd = () => {
    setIsClosing(false);
  };

  const menuItems = [
    { name: 'Dashboard', path: '/dashboard', icon: <SpaceDashboardIcon /> },
    { name: 'Category', path: '/category', icon: <CategoryIcon /> },
    { name: 'SubCategory', path: '/subcategory', icon: <ControlPointDuplicateIcon /> },
    { name: 'Q&A', path: '/qa', icon: <HelpOutlineIcon /> },
  ];

  const drawerContent = (
    <Box>
      <Toolbar sx={{ color: 'white', backgroundColor: '#0A1929', padding: '10px', display: 'flex', alignItems: 'center', justifyContent: 'center' }}>
        <Typography variant="h6" fontWeight="bold">
          Interview Portal
        </Typography>
      </Toolbar>

      <Divider />

      <List>
        {menuItems.map((item) => (
          <Link key={item.name} href={item.path} sx={{ textDecoration: 'none', color: 'inherit' }}>
            <ListItem disablePadding>
              <ListItemButton sx={{ padding: '12px', borderRadius: '8px', '&:hover': { backgroundColor: '#E3F2FD' } }}>
                <ListItemIcon sx={{ color: '#1565C0' }}>{item.icon}</ListItemIcon>
                <ListItemText primary={item.name} />
              </ListItemButton>
            </ListItem>
          </Link>
        ))}
      </List>

      <Divider />
    </Box>
  );

  return (
    <Box sx={{ display: 'flex', backgroundColor: '#F5F5F5', minHeight: '100vh' }}>
      <CssBaseline />

      {/* Top Bar */}
      <AppBar position="fixed" sx={{ width: { sm: `calc(100% - ${drawerWidth}px)` }, ml: { sm: `${drawerWidth}px` }, backgroundColor: '#0A1929' }}>
        <Toolbar sx={{ justifyContent: 'space-between' }}>
          <IconButton color="inherit" edge="start" onClick={handleDrawerToggle} sx={{ mr: 2, display: { sm: 'none' } }}>
            <MenuIcon />
          </IconButton>

          <Typography sx={{ fontSize: '18px', fontWeight: 'bold' }} variant="h6" noWrap>
            Dashboard
          </Typography>

          <IconButton color="inherit">
            <MeetingRoomIcon />
          </IconButton>
        </Toolbar>
      </AppBar>

      {/* Sidebar Navigation */}
      <Box component="nav" sx={{ width: { sm: drawerWidth }, flexShrink: { sm: 0 } }} aria-label="menu options">
        <Drawer
          variant="temporary"
          open={mobileOpen}
          onTransitionEnd={handleDrawerTransitionEnd}
          onClose={handleDrawerClose}
          ModalProps={{ keepMounted: true }}
          sx={{ display: { xs: 'block', sm: 'none' }, '& .MuiDrawer-paper': { width: drawerWidth, boxSizing: 'border-box' } }}
        >
          {drawerContent}
        </Drawer>

        <Drawer variant="permanent" sx={{ display: { xs: 'none', sm: 'block' }, '& .MuiDrawer-paper': { width: drawerWidth, boxSizing: 'border-box' } }} open>
          {drawerContent}
        </Drawer>
      </Box>

      {/* Main Content */}
      <Box component="main" sx={{ flexGrow: 1, p: 3, width: { sm: `calc(100% - ${drawerWidth}px)` }, backgroundColor: 'white', borderRadius: '8px', margin: '16px' }}>
        <Toolbar />
        {children}
      </Box>
    </Box>
  );
};

InterviewPortal.propTypes = {
  children: PropTypes.node,
};

export default InterviewPortal;
