    const method = editId ? 'patch' : 'post';
    const url = editId ? `https://interviewback-ucb4.onrender.com/category/${editId}` : 'https://interviewback-ucb4.onrender.com/category/create';

    axios[method](url, values, {
      headers: {
        Authorization: "eyJhbGciOiJIUzI1NiIsInR5cCI6IkpXVCJ9.eyJpZCI6IjY3ODA4YTE3MzRkZGY2ZjZlZGUyNTRmMSIsImlhdCI6MTc0MjYyMDUwM30._S8-zRqSVebO8IljXmp3vSa-IOpV2_bp-USjSzuVFP8"
      }
    })
      .then(() => {
        dataView();
        resetForm();
        handleClose();
      })
      .catch(console.error);
  };

  const dataView = () => {
    axios.get('https://interviewback-ucb4.onrender.com/category/', {
      headers: {
        Authorization: "eyJhbGciOiJIUzI1NiIsInR5cCI6IkpXVCJ9.eyJpZCI6IjY3ODA4YTE3MzRkZGY2ZjZlZGUyNTRmMSIsImlhdCI6MTc0MjYyMDUwM30._S8-zRqSVebO8IljXmp3vSa-IOpV2_bp-USjSzuVFP8"
      }
    })
      .then(res => setList(res.data.data))
      .catch(console.error);
  };

  const deleteData = id => {
    axios.delete(`https://interviewback-ucb4.onrender.com/category/${id}`, {
      headers: {
        Authorization: "eyJhbGciOiJIUzI1NiIsInR5cCI6IkpXVCJ9.eyJpZCI6IjY3ODA4YTE3MzRkZGY2ZjZlZGUyNTRmMSIsImlhdCI6MTc0MjYyMDUwM30._S8-zRqSVebO8IljXmp3vSa-IOpV2_bp-USjSzuVFP8"
      }
    })
      .then(dataView)
      .catch(console.error);
  };

  const editData = (id, row) => {
    handleClickOpen();
    setIni(row);
    setEditId(id);
  };

  const searchCategory = (e) => {
    const value = e.target.value;
    setSearchCat(value);
    axios.get(`https://interviewback-ucb4.onrender.com/category/?search=${value}`, {
      headers: {
        Authorization: "eyJhbGciOiJIUzI1NiIsInR5cCI6IkpXVCJ9.eyJpZCI6IjY3ODA4YTE3MzRkZGY2ZjZlZGUyNTRmMSIsImlhdCI6MTc0Mjc5Njg3OX0.VhjRg_oGm9skgXMpeoVr5BzXF_1CYCdgaGYn8WCL_aM"
      }
    })
      .then(res => setList(res.data.data))
      .catch(console.error);
  };

  const allSubCatUpdate = (filteredSubcategories) => {
    filteredSubcategories.forEach(sub => {
      axios.patch(`https://interviewback-ucb4.onrender.com/subcategory/${sub._id}`, {
        status: sub.status === 'on' ? 'off' : 'on',
      }, {
        headers: {
          Authorization: "eyJhbGciOiJIUzI1NiIsInR5cCI6IkpXVCJ9.eyJpZCI6IjY3ODA4YTE3MzRkZGY2ZjZlZGUyNTRmMSIsImlhdCI6MTc0Mjc5Njg3OX0.VhjRg_oGm9skgXMpeoVr5BzXF_1CYCdgaGYn8WCL_aM"
        }
      });
    });
  };

  const sub = id => {
    axios.get('https://interviewback-ucb4.onrender.com/subcategory/', {
      headers: {
        Authorization: "eyJhbGciOiJIUzI1NiIsInR5cCI6IkpXVCJ9.eyJpZCI6IjY3ODA4YTE3MzRkZGY2ZjZlZGUyNTRmMSIsImlhdCI6MTc0MjYyMDUwM30._S8-zRqSVebO8IljXmp3vSa-IOpV2_bp-USjSzuVFP8"
      }
    })
      .then(res => {
        const filtered = res.data.data.filter(item => item.categoryID?._id === id);
        allSubCatUpdate(filtered);
      })
      .catch(console.error);
  };

  const handleswitch = (e, id) => {
    axios.patch(`https://interviewback-ucb4.onrender.com/category/${id}`, {
      status: e.target.checked ? 'on' : 'off'
    }, {
      headers: {
        Authorization: "eyJhbGciOiJIUzI1NiIsInR5cCI6IkpXVCJ9.eyJpZCI6IjY3ODA4YTE3MzRkZGY2ZjZlZGUyNTRmMSIsImlhdCI6MTc0Mjc5Njg3OX0.VhjRg_oGm9skgXMpeoVr5BzXF_1CYCdgaGYn8WCL_aM"
      }
    })
      .then(() => {
        dataView();
        sub(id);
      })
      .catch(console.error);
  };

  useEffect(() => {
    dataView();
  }, []);

  return (
    <InteviewPortal>
      <Box display="flex" flexWrap="wrap" gap={2}>
        <TextField
          fullWidth
          label="Search Category"
          value={serchcat}
          onChange={searchCategory}
        />
        <Button onClick={handleClickOpen} sx={{ backgroundColor: "#0d47a1", color: "#fff", width: 200 }}>Add Category</Button>
      </Box>

      <BootstrapDialog open={open} onClose={handleClose}>
        <DialogTitle>Add Category</DialogTitle>
        <IconButton onClick={handleClose} sx={{ position: 'absolute', right: 8, top: 8 }}>
          <CloseIcon />
        </IconButton>
        <Formik initialValues={ini} enableReinitialize onSubmit={handleSubmit}>
          <Form>
            <DialogContent>
              <Field as={TextField} name="categoryName" label="Category" fullWidth />
            </DialogContent>
            <DialogActions>
              <Button type="submit" sx={{ backgroundColor: "#0d47a1", color: "#fff" }}>Submit</Button>
            </DialogActions>
          </Form>
        </Formik>
      </BootstrapDialog>

      <TableContainer component={Paper} sx={{ mt: 3 }}>
        <Table>
          <TableHead>
            <TableRow>
              <StyledTableCell>No.</StyledTableCell>
              <StyledTableCell align="center">Category Name</StyledTableCell>
              <StyledTableCell align="center">Status</StyledTableCell>
              <StyledTableCell align="center">Delete</StyledTableCell>
              <StyledTableCell align="center">Update</StyledTableCell>
            </TableRow>
          </TableHead>
          <TableBody>
            {list.map((row, i) => (
              <StyledTableRow key={row._id}>
                <StyledTableCell>{i + 1}</StyledTableCell>
                <StyledTableCell align="center">{row.categoryName}</StyledTableCell>
                <StyledTableCell align="center">
                  <Switch checked={row.status === 'on'} onChange={(e) => handleswitch(e, row._id)} />
                </StyledTableCell>
                <StyledTableCell align="center">
                  <Tooltip title="Delete">
                    <IconButton onClick={() => deleteData(row._id)} sx={{ color: red[600] }}>
                      <DeleteIcon />
                    </IconButton>
                  </Tooltip>
                </StyledTableCell>
                <StyledTableCell align="center">
                  <Tooltip title="Edit">
                    <IconButton onClick={() => editData(row._id, row)} sx={{ color: green[700] }}>
                      <EditIcon />
                    </IconButton>
                  </Tooltip>
                </StyledTableCell>
              </StyledTableRow>
            ))}
          </TableBody>
        </Table>
      </TableContainer>
    </InteviewPortal>
  );
};

export default Categary;
