import React, { useState, useEffect } from 'react';
import {
  Box, TextField, MenuItem, styled, Table, TableBody, TableCell, TableContainer, TableHead, TableRow, Paper, Button, FormControl, InputLabel, Select, Dialog, DialogTitle, DialogContent, DialogActions, IconButton, Typography
} from '@mui/material';
import { Formik, Form, Field } from 'formik';
import DeleteIcon from '@mui/icons-material/Delete';
import EditIcon from '@mui/icons-material/Edit';
import CloseIcon from '@mui/icons-material/Close';
import axios from 'axios';
import InteviewPortal from './InteviewPortal';

const BootstrapDialog = styled(Dialog)(({ theme }) => ({
  '& .MuiDialogContent-root': { padding: theme.spacing(2) },
  '& .MuiDialogActions-root': { padding: theme.spacing(1) },
}));

const StyledTableCell = styled(TableCell)(({ theme }) => ({
  [`&.${tableCellClasses.head}`]: {
    backgroundColor: '#1976d2',
    color: theme.palette.common.white,
  },
  [`&.${tableCellClasses.body}`]: { fontSize: 14 },
}));

const StyledTableRow = styled(TableRow)(({ theme }) => ({
  '&:nth-of-type(odd)': { backgroundColor: theme.palette.action.hover },
  '&:last-child td, &:last-child th': { border: 0 },
}));

const Tokan = "eyJhbGciOiJIUzI1NiIsInR5cCI6IkpXVCJ9.eyJpZCI6IjY3ODA4YTE3MzRkZGY2ZjZlZGUyNTRmMSIsImlhdCI6MTc0MzA1NTQyMn0.aZYxyCRQ1STR-TFhknYeX8hpE5jBi2jFSqTnt5RLXHU";

const QandA = () => {
  const [open, setOpen] = useState(false);
  const [ini, setIni] = useState({ questions: '', answer: '', subcategoryID: '' });
  const [list1, setList1] = useState([]);
  const [list2, setList2] = useState([]);
  const [editId, setEditId] = useState(null);

  const handleClickOpen = () => setOpen(true);
  const handleClose = () => setOpen(false);

  const handleSubmit = (values, { resetForm }) => {
    if (editId) {
      axios.patch(`https://interviewback-ucb4.onrender.com/questions/${editId}`, values, {
        headers: { Authorization: Tokan }
      })
        .then(() => {
          console.log('Update success');
          dataView();
          setEditId(null);
          resetForm();
        })
        .catch((err) => console.log(err));
    } else {
      axios.post('https://interviewback-ucb4.onrender.com/questions/create', values, {
        headers: { Authorization: Tokan }
      })
        .then(() => {
          console.log('Data entry success');
          dataView();
          resetForm();
        })
        .catch((error) => console.log(error));
    }
    handleClose();
  };

  const dataView = () => {
    axios.get('https://interviewback-ucb4.onrender.com/questions/', {
      headers: { Authorization: Tokan }
    })
      .then((res) => setList2(res.data.data))
      .catch((error) => console.log(error));
  };

  const handleFillsubcategory = () => {
    axios.get('https://interviewback-ucb4.onrender.com/subcategory/', {
      headers: { Authorization: Tokan }
    })
      .then((res) => setList1(res.data.data))
      .catch((error) => console.log(error));
  };

  const deleteData = (id) => {
    axios.delete(`https://interviewback-ucb4.onrender.com/questions/${id}`, {
      headers: { Authorization: Tokan }
    })
      .then(() => {
        console.log('Deleted successfully');
        dataView();
      })
      .catch((err) => console.log(err));
  };

  const editData = (id, row) => {
    handleClickOpen();
    setEditId(id);
    setIni({
      questions: row.questions,
      answer: row.answer,
      subcategoryID: row.subcategoryID?._id || '',
    });
  };

  useEffect(() => {
    dataView();
    handleFillsubcategory();
  }, []);

  return (
    <InteviewPortal>
      <Box sx={{ display: 'flex', justifyContent: 'space-between', p: 2 }}>
        <Button variant="contained" onClick={handleClickOpen}>
          ADD Q & A
        </Button>
      </Box>

      <BootstrapDialog onClose={handleClose} open={open}>
        <DialogTitle>
          {editId ? "Edit Question" : "Add Question"}
          <IconButton sx={{ position: 'absolute', right: 8, top: 8 }} onClick={handleClose}>
            <CloseIcon />
          </IconButton>
        </DialogTitle>
        <Formik enableReinitialize initialValues={ini} onSubmit={handleSubmit}>
          <Form>
            <DialogContent dividers>
              <Field as={TextField} name="questions" label="Questions" fullWidth margin="dense" />
              <Field as={TextField} name="answer" label="Answer" fullWidth margin="dense" />
              <FormControl fullWidth margin="dense">
                <InputLabel>Subcategory</InputLabel>
                <Select name="subcategoryID" value={ini.subcategoryID} onChange={(e) => setIni({ ...ini, subcategoryID: e.target.value })}>
                  {list1.map((row) => <MenuItem key={row._id} value={row._id}>{row.subCategoryname}</MenuItem>)}
                </Select>
              </FormControl>
            </DialogContent>
            <DialogActions>
              <Button type="submit" variant="contained" color="primary">Submit</Button>
            </DialogActions>
          </Form>
        </Formik>
      </BootstrapDialog>

      <TableContainer component={Paper}>
        <Table>
          <TableHead>
            <TableRow>
              <StyledTableCell>No.</StyledTableCell>
              <StyledTableCell>Question</StyledTableCell>
              <StyledTableCell>Answer</StyledTableCell>
              <StyledTableCell>Subcategory</StyledTableCell>
              <StyledTableCell>Actions</StyledTableCell>
            </TableRow>
          </TableHead>
          <TableBody>
            {list2.map((row, i) => (
              <StyledTableRow key={i}>
                <StyledTableCell>{i + 1}</StyledTableCell>
                <StyledTableCell>{row.questions}</StyledTableCell>
                <StyledTableCell>{row.answer}</StyledTableCell>
                <StyledTableCell>{row.subcategoryID?.subCategoryname || 'N/A'}</StyledTableCell>
                <StyledTableCell>
                  <IconButton onClick={() => editData(row._id, row)}><EditIcon /></IconButton>
                  <IconButton onClick={() => deleteData(row._id)}><DeleteIcon /></IconButton>
                </StyledTableCell>
              </StyledTableRow>
            ))}
          </TableBody>
        </Table>
      </TableContainer>
    </InteviewPortal>
  );
};

export default QandA;
