import React, { useEffect, useState } from "react";
import {
  Box,
  TextField,
  MenuItem,
  Table,
  TableBody,
  TableCell,
  TableContainer,
  TableHead,
  TableRow,
  Paper,
  Button,
  Switch,
  Dialog,
  DialogTitle,
  DialogContent,
  DialogActions,
  IconButton,
  FormControlLabel,
} from "@mui/material";
import { styled } from "@mui/material/styles";
import CloseIcon from "@mui/icons-material/Close";
import DeleteIcon from '@mui/icons-material/Delete';
import EditIcon from '@mui/icons-material/Edit';
import { Formik, Form, Field } from "formik";
import axios from "axios";
import InteviewPortal from "./InteviewPortal";

const BootstrapDialog = styled(Dialog)(({ theme }) => ({
  "& .MuiDialogContent-root": {
    padding: theme.spacing(2),
  },
  "& .MuiDialogActions-root": {
    padding: theme.spacing(1),
  },
}));

const StyledTableCell = styled(TableCell)(({ theme }) => ({
  [`&.${tableCellClasses.head}`]: {
    backgroundColor: theme.palette.primary.main,
    color: theme.palette.common.white,
  },
  [`&.${tableCellClasses.body}`]: {
    fontSize: 14,
  },
}));

const StyledTableRow = styled(TableRow)(({ theme }) => ({
  "&:nth-of-type(odd)": {
    backgroundColor: theme.palette.action.hover,
  },
  "&:last-child td, &:last-child th": {
    border: 0,
  },
}));

const Categary = () => {
  const [open, setOpen] = useState(false);
  const [ini, setIni] = useState({ categoryName: '' });
  const [list, setList] = useState([]);
  const [editId, setEditId] = useState(null);
  const [serchcat, setSearchCat] = useState('');

  const token = "your_token_here";

  const handleClickOpen = () => setOpen(true);
  const handleClose = () => {
    setOpen(false);
    setEditId(null);
    setIni({ categoryName: '' });
  };

  const handleSubmit = (values, { resetForm }) => {
    const url = editId
      ? `https://interviewback-ucb4.onrender.com/category/${editId}`
      : 'https://interviewback-ucb4.onrender.com/category/create';
    const method = editId ? axios.patch : axios.post;

    method(url, values, {
      headers: { Authorization: token },
    })
      .then(() => {
        dataView();
        handleClose();
      })
      .catch(console.error);

    resetForm();
  };

  const dataView = () => {
    axios.get('https://interviewback-ucb4.onrender.com/category/', {
      headers: { Authorization: token },
    })
      .then(res => setList(res.data.data))
      .catch(console.error);
  };

  const deleteData = id => {
    axios.delete(`https://interviewback-ucb4.onrender.com/category/${id}`, {
      headers: { Authorization: token },
    })
      .then(dataView)
      .catch(console.error);
  };

  const editData = (id, row) => {
    setIni(row);
    setEditId(id);
    setOpen(true);
  };

  const searchCategory = e => {
    const value = e.target.value;
    setSearchCat(value);

    axios.get(`https://interviewback-ucb4.onrender.com/category/?search=${value}`, {
      headers: { Authorization: token },
    })
      .then(res => setList(res.data.data))
      .catch(console.error);
  };

  const sub = id => {
    axios.get('https://interviewback-ucb4.onrender.com/subcategory/', {
      headers: { Authorization: token },
    })
      .then(item => {
        const filtered = item.data.data.filter(it => it.categoryID?._id === id);
        allSubCatUpdate(filtered);
      })
      .catch(console.error);
  };

  const allSubCatUpdate = subcategories => {
    subcategories.forEach(subcategory => {
      axios.patch(`https://interviewback-ucb4.onrender.com/subcategory/${subcategory._id}`, {
        status: subcategory.status === 'on' ? 'off' : 'on',
      }, {
        headers: { Authorization: token },
      }).catch(console.error);
    });
  };

  const handleswitch = (e, id) => {
    axios.patch(`https://interviewback-ucb4.onrender.com/category/${id}`, {
      status: e.target.checked ? 'on' : 'off',
    }, {
      headers: { Authorization: token },
    })
      .then(() => {
        dataView();
        sub(id);
      })
      .catch(console.error);
  };

  useEffect(dataView, []);

  return (
    <InteviewPortal>
      <Box className="p-4 space-y-4">
        <Box className="flex flex-col md:flex-row md:items-end gap-4">
          <TextField
            label="Search Category"
            value={serchcat}
            onChange={searchCategory}
            fullWidth
          />
          <Button
            onClick={handleClickOpen}
            variant="contained"
            className="h-12"
          >
            Add Category
          </Button>
        </Box>

        <TableContainer component={Paper} className="rounded-xl shadow-md">
          <Table>
            <TableHead>
              <TableRow>
                <StyledTableCell>No.</StyledTableCell>
                <StyledTableCell align="center">Category Name</StyledTableCell>
                <StyledTableCell align="center">Status</StyledTableCell>
                <StyledTableCell align="center">Delete</StyledTableCell>
                <StyledTableCell align="center">Update</StyledTableCell>
              </TableRow>
            </TableHead>
            <TableBody>
              {list.map((row, i) => (
                <StyledTableRow key={row._id}>
                  <StyledTableCell>{i + 1}</StyledTableCell>
                  <StyledTableCell align="center">{row.categoryName}</StyledTableCell>
                  <StyledTableCell align="center">
                    <FormControlLabel
                      control={<Switch checked={row.status === 'on'} onChange={(e) => handleswitch(e, row._id)} />}
                      label={row.status === 'on' ? 'Active' : 'Inactive'}
                    />
                  </StyledTableCell>
                  <StyledTableCell align="center">
                    <IconButton color="error" onClick={() => deleteData(row._id)}>
                      <DeleteIcon />
                    </IconButton>
                  </StyledTableCell>
                  <StyledTableCell align="center">
                    <IconButton color="primary" onClick={() => editData(row._id, row)}>
                      <EditIcon />
                    </IconButton>
                  </StyledTableCell>
                </StyledTableRow>
              ))}
            </TableBody>
          </Table>
        </TableContainer>

        <BootstrapDialog onClose={handleClose} open={open}>
          <DialogTitle>
            {editId ? 'Edit Category' : 'Add Category'}
            <IconButton
              aria-label="close"
              onClick={handleClose}
              sx={{ position: 'absolute', right: 8, top: 8, color: 'grey.500' }}
            >
              <CloseIcon />
            </IconButton>
          </DialogTitle>
          <Formik initialValues={ini} onSubmit={handleSubmit} enableReinitialize>
            <Form>
              <DialogContent dividers>
                <Field
                  as={TextField}
                  name="categoryName"
                  label="Category Name"
                  fullWidth
                  required
                />
              </DialogContent>
              <DialogActions>
                <Button type="submit" variant="contained" color="primary">
                  {editId ? 'Update' : 'Submit'}
                </Button>
              </DialogActions>
            </Form>
          </Formik>
        </BootstrapDialog>
      </Box>
    </InteviewPortal>
  );
};

export default Categary;
