import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { Formik, Form, Field } from 'formik';
import {
  Box,
  Button,
  Dialog,
  DialogTitle,
  DialogContent,
  DialogActions,
  IconButton,
  TextField,
  Table,
  TableBody,
  TableCell,
  TableContainer,
  TableHead,
  TableRow,
  Paper,
  MenuItem,
  FormControl,
  InputLabel,
  Select,
} from '@mui/material';
import { styled } from '@mui/material/styles';
import CloseIcon from '@mui/icons-material/Close';
import DeleteIcon from '@mui/icons-material/Delete';
import EditIcon from '@mui/icons-material/Edit';
import InteviewPortal from './InteviewPortal';

const BootstrapDialog = styled(Dialog)(({ theme }) => ({
  '& .MuiDialogContent-root': { padding: theme.spacing(2) },
  '& .MuiDialogActions-root': { padding: theme.spacing(1) },
}));

const SubCategory = () => {
  const [open, setOpen] = useState(false);
  const [categories, setCategories] = useState([]);
  const [subCategories, setSubCategories] = useState([]);
  const [editId, setEditId] = useState(null);
  const [initialValues, setInitialValues] = useState({ subCategoryname: '', categoryID: '' });

  const fetchCategories = () => {
    axios
      .get('https://interviewback-ucb4.onrender.com/category/', {
        headers: { Authorization: 'YOUR_TOKEN' },
      })
      .then((res) => setCategories(res.data.data))
      .catch((err) => console.error(err));
  };

  const fetchSubCategories = () => {
    axios
      .get('https://interviewback-ucb4.onrender.com/subcategory/', {
        headers: { Authorization: 'YOUR_TOKEN' },
      })
      .then((res) => setSubCategories(res.data.data))
      .catch((err) => console.error(err));
  };

  useEffect(() => {
    fetchCategories();
    fetchSubCategories();
  }, []);

  const handleOpen = () => {
    setInitialValues({ subCategoryname: '', categoryID: '' });
    setEditId(null);
    setOpen(true);
  };

  const handleClose = () => setOpen(false);

  const handleSubmit = (values, { resetForm }) => {
    const request = editId
      ? axios.patch(`https://interviewback-ucb4.onrender.com/subcategory/${editId}`, values, { headers: { Authorization: 'YOUR_TOKEN' } })
      : axios.post('https://interviewback-ucb4.onrender.com/subcategory/create', values, { headers: { Authorization: 'YOUR_TOKEN' } });

    request
      .then(() => {
        fetchSubCategories();
        resetForm();
        handleClose();
      })
      .catch((err) => console.error(err));
  };

  const handleDelete = (id) => {
    axios
      .delete(`https://interviewback-ucb4.onrender.com/subcategory/${id}`, { headers: { Authorization: 'YOUR_TOKEN' } })
      .then(() => fetchSubCategories())
      .catch((err) => console.error(err));
  };

  const handleEdit = (id, row) => {
    setEditId(id);
    setInitialValues({ subCategoryname: row.subCategoryname, categoryID: row.categoryID?._id || '' });
    setOpen(true);
  };

  return (
    <InteviewPortal>
      <Box display="flex" justifyContent="space-between" mb={2}>
        <Button variant="contained" onClick={handleOpen}>
          Add Sub-Category
        </Button>
      </Box>

      <TableContainer component={Paper}>
        <Table>
          <TableHead>
            <TableRow>
              <TableCell>No.</TableCell>
              <TableCell>Sub-Category Name</TableCell>
              <TableCell>Category Name</TableCell>
              <TableCell>Actions</TableCell>
            </TableRow>
          </TableHead>
          <TableBody>
            {subCategories.map((row, index) => (
              <TableRow key={row._id}>
                <TableCell>{index + 1}</TableCell>
                <TableCell>{row.subCategoryname}</TableCell>
                <TableCell>{row.categoryID?.categoryName}</TableCell>
                <TableCell>
                  <IconButton onClick={() => handleEdit(row._id, row)}>
                    <EditIcon />
                  </IconButton>
                  <IconButton onClick={() => handleDelete(row._id)}>
                    <DeleteIcon />
                  </IconButton>
                </TableCell>
              </TableRow>
            ))}
          </TableBody>
        </Table>
      </TableContainer>

      <BootstrapDialog open={open} onClose={handleClose}>
        <DialogTitle>Add/Edit Sub-Category</DialogTitle>
        <IconButton onClick={handleClose} sx={{ position: 'absolute', right: 8, top: 8 }}>
          <CloseIcon />
        </IconButton>
        <Formik initialValues={initialValues} enableReinitialize onSubmit={handleSubmit}>
          <Form>
            <DialogContent>
              <Field as={TextField} name="subCategoryname" label="Sub-Category Name" fullWidth margin="dense" />
              <FormControl fullWidth margin="dense">
                <InputLabel>Category</InputLabel>
                <Field as={Select} name="categoryID" fullWidth>
                  {categories.map((cat) => (
                    <MenuItem key={cat._id} value={cat._id}>
                      {cat.categoryName}
                    </MenuItem>
                  ))}
                </Field>
              </FormControl>
            </DialogContent>
            <DialogActions>
              <Button type="submit" variant="contained">
                Submit
              </Button>
            </DialogActions>
          </Form>
        </Formik>
      </BootstrapDialog>
    </InteviewPortal>
  );
};

export default SubCategory;
